import React, { Component } from 'react';
import { Grid, Row, Col } from 'react-flexbox-grid';
import { connect } from 'react-redux';
import moment from 'moment';
import { addPost } from '../../redux/actions';
import './styles.scss';

class FormState extends Component {
  state = {
    text: ""
  }

  handleChange = (event) => {
    const { target: { value} } = event;
    this.setState({ text: value });
  }

  addState = () => {
    const { text } = this.state;
    this.props.addPost({
      text,
      date: moment().format('YYYY-MM-DD HH:mm:ss'),
      name: this.props.name
    });
    this.setState({ text: "" });
  }

  render() {
    return (
      <Grid fluid className="content_publish">
        <Row>
          <Col xs={12}>
            <textarea
              className="text_box" 
              rows={3} 
              placeholder={this.props.name === "" ? "Escribe primero tu nombre ↑" : "¿Qué estás pensando?" }
              onChange={this.handleChange}
              value={this.state.text}
              disabled={this.props.name === ""}
            ></textarea>
            <hr></hr>
          </Col>
          <Col xs={12}>
            <Row end="xs">
              <Col xs={6}>
                <button
                  className="button_publish" 
                  onClick={ () => this.addState() }
                  disabled={this.props.name === ""}
                >
                  Publicar
                </button>
              </Col>
            </Row>
          </Col>
        </Row>
      </Grid>
    );
  }
}

function mapStateToProps(state) {
  return {
    name: state.name
  }
}

export default connect(mapStateToProps, { addPost })(FormState);
