import React, { Component } from "react";
import { Grid, Row, Col } from 'react-flexbox-grid';
import { connect } from 'react-redux';
import { addName } from '../../redux/actions';
import  user  from '../../resources/image/profile.png'

import "./styles.scss";

class Navbar extends Component {
  state = {
    name: "",
  }
  handleChange = (e) => {
    const value = e.target.value;
    this.setState({ name: value });
  }
  save = () => {
    if (this.state.name !== "") {

      this.props.addName(this.state.name);
    }
  };
    render(){
      return(
        <div className="topnav">
          <Grid fluid>
            <Row>
              <Col xs={12} sm={12} md={6}>
                  
                    <div className="title">
                      Domicilios Test
                    </div>
              </Col>
              <Col xs={12} sm={12} md={6}>
                  <div className="content-input">
                    {
                      this.props.name === "" ? 
                      (
                        <div>
                          <input placeholder="Pon tu Nombre aqui!" onChange={this.handleChange} value={this.state.name} />
                          <button onClick={() => this.save()}>Aceptar</button>
                        </div>
                      ) : <div className="content-img"><img className="imguser" src={user}></img> <p>Hola! {this.props.name}</p></div>
                    }
                  </div>
              </Col>
            </Row>
          </Grid>
        </div>
      );
    }
  }



function mapStateToProps(state) {
  return {
    name: state.name
  }
}

export default connect(mapStateToProps, { addName })(Navbar);
